    private void filebrowsebtn3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_filebrowsebtn3ActionPerformed
// TODO add your handling code here:
            FileDialog fd=new FileDialog(this,"Open File",FileDialog.LOAD);
            fd.show();
            String s1=fd.getDirectory();
            String s2=fd.getFile();
            if(s1!=null&&s2!=null)
            targetfilepath.setText(s1+s2);
        
    }//GEN-LAST:event_filebrowsebtn3ActionPerformed

