    private void hidebtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hidebtnActionPerformed
// TODO add your handling code here:
        String pass=password.getText();
        String sp=sourcepath.getText();
        String bmpp=bmpfilepath.getText();
        String tp=targetfilepath.getText();
        
        if(pass.length()>0&&sp.length()>0&&bmpp.length()>0&&tp.length()>0)
        {
            try{
            FileInputStream f=new FileInputStream(sp);
            byte b[]=new byte[f.available()];
            f.read(b);
            f.close();
            String m=new String(b);
            
            filesize.setText(""+m.length()+" Bytes");
            File f1=new File(bmpp);
            bmpsize.setText(""+f1.length()+" Bytes");
            long capacity=(f1.length()-54)-32*8-4*8;
            capacity=capacity/8;
            filecapacity.setText(""+capacity+" Bytes");
            showmsg.setText("Hiding Message");
            
            Hide.Stegano(bmpp, tp,pass, m);
            }catch(Exception e)
            {
                    System.out.println(e);
            }
            showmsg.setText("Process Completed");           
        }
        else
        {
            System.out.println("Hello");
            showmsg.setText("Failed: Insufficient Data");
        }
    }//GEN-LAST:event_hidebtnActionPerformed

